import { takeEvery, put, call } from 'redux-saga/effects';
import { httpRequest } from '../services';
import {
  updateHotDogStart,
  updateHotDogFail,
  updateHotDogSuccess,
} from '../redux/actions';

function* callUpdateHotDog({ payload }) {
  try {
    console.log(payload);
    const data = yield call(httpRequest, `http://localhost:3080/update/${payload.id}`, 'PUT', payload.data);
    yield put(updateHotDogSuccess(data));
  } catch (e) {
    console.log('Something is going wrong!')
    yield call(updateHotDogFail);
  }
}

export function* updateHotDogSagas() {
  yield takeEvery(updateHotDogStart, callUpdateHotDog);
}
