import React from 'react';
import { connect } from 'react-redux';
import { useForm } from 'react-hook-form';
import { showEditForm } from '../../redux/actions'
import { editFormSelector } from '../../redux/selectors';
import { deleteHotDogStart, updateHotDogStart } from '../../redux/actions';

import './hot-dog-item.css';

const HotDogItem = ({data, isVisibleEditForm, showEditForm, deleteHotDogStart, updateHotDogStart}) => {


    const { register, handleSubmit, errors, setValue } = useForm();

    const { hotdog_id: id, title, price, description, img } = data;

    useEffect(() => {
        setValue('name', title);
        setValue('price', price);
        setValue('description', description);
        setValue('img', img);
    }, [title, price, description, img]);

    return (
        <li className='hot-dog-item'>
            <img src={img} alt=""/>
            {isVisibleEditForm !== id &&
                <>
                    <h3 className='hot-dog-item__title'>{title}</h3>
                    <p className='hot-dog-item__price'>{price}</p>
                    <p className='hot-dog-item__description'>{description}</p>
                    <button onClick={() => showEditForm(id)} className='hot-dog-item__btn'>Edit</button>
                </>
            }

            {isVisibleEditForm === id && 
                <form className='edit-form' onSubmit={handleSubmit((data) => updateHotDogStart({data, id}))}>
                    <input name='img' type='text' value={img} ref={register({ required: true })} />
                    <input name='title' type='text' value={title} ref={register({ required: true })}/>
                    <input name='price' type='text' value={price}  ref={register({ required: true })}/>
                    <textarea 
                        name='description' 
                        value={description} 
                        className='edit-form__description' 
                        ref={register({ required: true })}>
                    </textarea>
                    <button  type='button' className='hot-dog-item__btn' onClick={() => deleteHotDogStart(id)} >Delete</button>
                    <button type='submit' className='hot-dog-item__btn'>Upgrade</button>
                </form>
            }
        </li>
    );
}

const mapStateToProps = (state) => {
    return {
        isVisibleEditForm: editFormSelector(state)
    }
}

const mapDispatchToProps = {
    showEditForm,
    deleteHotDogStart,
    updateHotDogStart
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(HotDogItem);
